const mq = require('ibmmq');
const MQC = mq.MQC;
function openQueue(client, MQDetails) {
    let openOptions = MQC.MQOO_INPUT_AS_Q_DEF;
    let objectDescriptor = new mq.MQOD();
    objectDescriptor.ObjectName = MQDetails.queue;
    objectDescriptor.ObjectType = MQC.MQOT_Q;
    return new Promise((resolve, reject) => {
        mq.Open(client, objectDescriptor, openOptions, function (err, queueClient) {
            if (err) {
                logger.error('Error Subscribing IBM MQ Queue :', err);
                reject(err);
            } else {
                logger.info('IBM MQ Queue Opened :', MQDetails.queue);
                resolve(queueClient);
            }
        });
    })
}
function getMessage(queueClient) {
    let buffer = Buffer.alloc(inputData.messageSize || 1024);
    let messageDescriptor = new mq.MQMD();
    let getMessageOptions = new mq.MQGMO();
    getMessageOptions.Options = MQC.MQGMO_NO_SYNCPOINT |
        MQC.MQGMO_NO_WAIT |
        MQC.MQGMO_CONVERT |
        MQC.MQGMO_FAIL_IF_QUIESCING;
    return new Promise((resolve, reject) => {
        mq.GetSync(queueClient, messageDescriptor, getMessageOptions, buffer, function (err, len) {
            logger.info(messageDescriptor, buffer);
            if (err) {
                reject(err);
                if (err.mqrc == MQC.MQRC_NO_MSG_AVAILABLE) {
                    logger.info('no more messages');
                } else {
                    logger.warn('MQGET failed with ' + err.mqrc);
                }
            } else {
                resolve(Buffer.from(buffer));
                logger.info('Found Message in Queue');
            }
        });
    })
}
const queueClient = await openQueue(connectorData.mqClient, { queue: inputData.queue });
setInterval(async () => {
    const result = await getMessage(queueClient);
    StartProcess({ data: result });
}, 100);

const axios = require('axios');
const axiosRetry = require('axios-retry').default;
const axiosConfig = {};
try {
    axiosRetry(axios, {
        retries: inputData.retries || process.env.HTTPCLIENT_RETRIES || 0,
        retryDelay: (retryCount, error) => {
            logger.info(`[RETRY] Attempt #${retryCount} - Reason: ${error?.code || error?.message}`);
            return (inputData.retryDelay || process.env.HTTPCLIENT_RETRY_DELAY || 1000);
        },
        onRetry: (retryCount, error, requestConfig) => {
            logger.error(`[RETRYING] Retry #${retryCount} for ${requestConfig?.url} - Error: ${error?.message}`);
        },
        retryCondition: (error) => {
            // Log the error to understand what's happening
            logger.info('[DEBUG] Checking retry condition for error:', {
                status: error?.response?.status,
                code: error?.code,
                message: error?.message
            });
            // Retry on 401, network errors, and 5xx errors
            return axiosRetry.isNetworkOrIdempotentRequestError(error) ||
                (error?.response?.status >= 400 && error?.response?.status <= 599);
        },
        shouldResetTimeout: true // Reset the timeout between retries
    });
    axiosConfig.responseType = 'arraybuffer';
    axiosConfig.method = inputData.method;
    axiosConfig.url = `${connectorData.protocol}://${connectorData.host}:${connectorData.port}${inputData.path}`;
    axiosConfig.data = inputData.data ? Buffer.from(inputData.data) : undefined;
    axiosConfig.params = inputData.query;
    axiosConfig.headers = inputData.headers;
    axiosConfig.timeout = inputData.timeout || process.env.HTTPCLIENT_TIMEOUT || 60000;
    logger.info(`Making HTTP [${axiosConfig.method}] Request at [${axiosConfig.url}]`);
    logger.trace(JSON.stringify(axiosConfig));
    axiosConfig.validateStatus = function (status) {
        if (inputData.throwErrorOn4xx) {
            return status >= 200 && status < 300;
        } else {
            return status >= 200 && status < 500;
        }
    };
    let response = await axios(axiosConfig);
    return { statusCode: response.status, data: response.data, headers: response.headers };
}
catch (err) {
    if (err.response) {
        const error = new Error(`Axios request failed: ${err.response.status} - ${err.response.statusText}`);
        error.statusCode = err.response.status;
        error.data = err.response.data;
        error.stack = err.stack;
        throw error;

    } else if (err.code === 'ECONNABORTED') {
        const error = new Error('Request timeout');
        error.statusCode = 408;
        error.code = err.code;
        error.stack = err.stack;
        throw error;

    } else {
        throw err;
    }
}

const oracledb = require('oracledb');
