} catch (err) {
    throw err
} finally {
    if (connection) {
        connection.close();
    }
}

function withTimeout(promise, ms) {
    return Promise.race([
        promise,
        new Promise((_, reject) =>
            setTimeout(() => reject(new Error('Query timeout exceeded: ' + ms + 'ms')), ms)
        ),
    ]);
}

