const jsonata = require('jsonata');

function setNestedValue(obj, path, value) {
    const parts = path.split('.');
    let current = obj;
    for (let i = 0; i < parts.length - 1; i++) {
        const part = parts[i];
        if (!(part in current)) current[part] = {};
        current = current[part];
    }
    current[parts[parts.length - 1]] = value;
}

function getNestedValue(obj, path) {
    const parts = path.split('.');
    let current = obj;
    for (const part of parts) {
        if (current === null || current === undefined) return undefined;
        current = current[part];
    }
    return current;
}

function mergeArrayFieldMappings(fieldMappings) {
    if (fieldMappings.size === 0) return [];

    const fieldNames = Array.from(fieldMappings.keys());
    const maxLength = Math.max(...fieldNames.map(name => fieldMappings.get(name).length));
    const result = [];

    for (let i = 0; i < maxLength; i++) {
        const mergedObject = {};
        for (const fieldName of fieldNames) {
            const fieldArray = fieldMappings.get(fieldName);
            if (i < fieldArray.length && fieldArray[i] !== null && fieldArray[i] !== undefined) {
                mergedObject[fieldName] = fieldArray[i];
            }
        }
        if (Object.keys(mergedObject).length > 0) {
            result.push(mergedObject);
        }
    }
    return result;
}

async function transformData(sourceData, targetData, mappings) {
    const result = {};
    const arrayFieldMappings = new Map();

    for (const mapping of mappings) {
        try {
            const expression = jsonata(mapping.transformationCode);
            const transformedValue = await expression.evaluate(sourceData);

            if (transformedValue !== undefined && transformedValue !== null) {
                const targetValue = getNestedValue(targetData, mapping.targetPath);
                const targetIsArray = Array.isArray(targetValue);
                const targetPathParts = mapping.targetPath.split('.');
                const isFieldMapping = targetPathParts.length > 1 && !targetIsArray;

                if (isFieldMapping && Array.isArray(transformedValue)) {
                    const fieldName = targetPathParts[targetPathParts.length - 1];
                    const arrayPath = targetPathParts.slice(0, -1).join('.');

                    if (!arrayFieldMappings.has(arrayPath)) {
                        arrayFieldMappings.set(arrayPath, new Map());
                    }
                    arrayFieldMappings.get(arrayPath).set(fieldName, transformedValue);
                } else {
                    setNestedValue(result, mapping.targetPath, transformedValue);
                }
            }
        } catch (error) {
            console.error(`Transformation failed for ${mapping.targetPath}:`, error);
        }
    }

    for (const [arrayPath, fieldMappings] of arrayFieldMappings) {
        const mergedArray = mergeArrayFieldMappings(fieldMappings);
        setNestedValue(result, arrayPath, mergedArray);
    }

    console.log(JSON.stringify(result));
    return result;
}

