const mq = require('ibmmq');
const MQC = mq.MQC;
function openQueue(client, MQDetails) {
    let openOptions = MQC.MQOO_OUTPUT;
    let objectDescriptor = new mq.MQOD();
    objectDescriptor.ObjectName = MQDetails.queue;
    objectDescriptor.ObjectType = MQC.MQOT_Q;
    return new Promise((resolve, reject) => {
        mq.Open(client, objectDescriptor, openOptions, function (err, queueClient) {
            if (err) {
                logger.error('Error Opening IBM MQ Topic :', err);
                reject(err);
            } else {
                logger.info('IBM MQ Topic Opened :', MQDetails.queue);
                resolve(queueClient);
            }
        });
    })
}
function putMessage(client, message) {
    var messageDescriptor = new mq.MQMD(); // Defaults are fine.
    var putMessageOptions = new mq.MQPMO();
    putMessageOptions.Options = MQC.MQPMO_NO_SYNCPOINT |
        MQC.MQPMO_NEW_MSG_ID |
        MQC.MQPMO_NEW_CORREL_ID;
    messageDescriptor.Persistence = MQC.MQPER_PERSISTENT;
    return new Promise((resolve, reject) => {
        mq.Put(client, messageDescriptor, putMessageOptions, message, function (err) {
            if (err) {
                logger.error('Error While Publishing message on IBM MQ :', err);
                reject(err);
            } else {
                const messageId = Buffer.from(messageDescriptor.MsgId).toString('hex')
                logger.info('MsgId: ', messageId);
                logger.info("Message Published to IBM MQ");
                resolve({ messageId });
            }
        });
    })
}
const queueClient = await openQueue(connectorData.mqClient, { queue: inputData.queue });
const result = await putMessage(queueClient, inputData.data);
return result;

const solace = require('solclientjs');
const consumer = connectorData.solaceSession.createMessageConsumer({
    queueDescriptor: { name: inputData.queue, type: solace.DestinationType.QUEUE },
    acknowledgeMode: solace.MessageConsumerAcknowledgeMode.CLIENT, // Required for late acknowledgment
});
consumer.on(solace.MessageConsumerEventName.UP, () => {
    console.log(`Listening on queue: ${inputData.queue}`);
});
consumer.on(solace.MessageConsumerEventName.CONNECT_FAILED_ERROR, (error) => {
    console.error(`Queue connection failed: ${error}`);
});
consumer.on(solace.MessageConsumerEventName.DOWN, () => {
    console.warn(`Queue consumer down: ${inputData.queue}`);
});
consumer.on(solace.MessageConsumerEventName.MESSAGE, (message) => {
    const payload = {
        message: message,
        type: null,
        data: null
    };

    try {
        if (message.getBinaryAttachment()) {
            payload.data = message.getBinaryAttachment();
            payload.type = 'binary';
        } else if (message.getXmlContentDecoded()) {
            payload.data = message.getXmlContentDecoded();
            payload.type = 'xml-decoded';
        } else if (message.getXmlContent()) {
            payload.data = message.getXmlContent();
            payload.type = 'xml-raw';
        } else if (message.getText()) {
            payload.data = message.getText();
            payload.type = 'text';
        } else {
            console.warn('Solace Consume: Received unknown message type');
        }
    } catch (err) {
        payload.data = null;
        payload.type = 'error';
        payload.error = `Failed to parse message: ${err.message}`;
        console.error('Error while processing Solace message:', err);
    }

    StartProcess(payload);
});
consumer.connect();

