const axios = require('axios');
const axiosRetry = require('axios-retry').default;

try {
    axiosRetry(axios, {
        retries: inputData.retries || process.env.CALLFLOW_RETRIES || 0,
        retryDelay: (retryCount, error) => {
            logger.info(`[RETRY] Attempt #${retryCount} - Reason: ${error?.code || error?.message}`);
            return (inputData.retryDelay || process.env.CALLFLOW_RETRY_DELAY || 1000);
        },
        onRetry: (retryCount, error, requestConfig) => {
            logger.error(`[RETRYING] Retry #${retryCount} for ${requestConfig?.url} - Error: ${error?.message}`);
        },
        retryCondition: (error) => {
            // Log the error to understand what's happening
            logger.info('[DEBUG] Checking retry condition for error:', {
                status: error?.response?.status,
                code: error?.code,
                message: error?.message
            });
            // Retry on 401, network errors, and 5xx errors
            return axiosRetry.isNetworkOrIdempotentRequestError(error) ||
                (error?.response?.status >= 500 && error?.response?.status <= 599);
        },
        shouldResetTimeout: true // Reset the timeout between retries
    });

    if (!inputData.headers) {
        inputData.headers = {};
    }

    if (inputData.token) {
        inputData.headers['Authorization'] = inputData.token;
    }

    let isDataArray = _.isArray(inputData.data);

    if (inputData.forEachRecord && isDataArray) {
        let promises = inputData.data.map(async (item) => {
            try {
                const resp = await axios({
                    method: inputData.method || connectorData.method || 'POST',
                    url: connectorData.api,
                    headers: inputData.headers,
                    data: item,
                    responseType: inputData.responseType || 'json',
                    timeout: inputData.timeout || 60000,
                });
                return {
                    statusCode: resp.status,
                    data: resp.data,
                    headers: resp.headers
                };
            } catch (err) {
                logger.error('got error response from flow :: ', connectorData.flow.name);
                logger.error(err);
                return {
                    statusCode: err.response?.status || 500,
                    data: err.response?.data || err.message,
                    headers: err.response?.headers || {},
                    stack: err?.stack
                };
            }
        });

        let allData = await Promise.all(promises);
        return { statusCode: 200, data: allData };

    } else {
        const resp = await axios({
            method: inputData.method || connectorData.method || 'POST',
            url: connectorData.api,
            headers: inputData.headers,
            data: inputData.data,
            responseType: inputData.responseType || 'json',
            timeout: inputData.timeout || process.env.CALLFLOW_TIMEOUT || 60000,
            validateStatus: function (status) {
                if (inputData.throwErrorOn4xx) {
                    return status >= 200 && status < 300;
                } else {
                    return status >= 200 && status < 500;
                }
            }
        });

        return { statusCode: resp.status, data: resp.data };
    }

} catch (err) {
