const Imap = require('imap');
const { simpleParser } = require('mailparser');
const imapConfig = {
    user: connectorData.user,
    password: connectorData.password,
    host: connectorData.host,
    port: connectorData.port,
    tls: connectorData.tls || true,
    tlsOptions: {
        rejectUnauthorized: false,
    },
};

function fetchEmails() {
    return new Promise((resolve, reject) => {
        const messages = [];
        const imap = new Imap(imapConfig);
        imap.once('ready', function () {
            logger.info('Looking into mailbox - ', inputData.mailbox);
            imap.openBox(inputData.mailbox, true, (err, box) => {
                if (err) {
                    reject(err);
                    return;
                }
                logger.info('Searching for emails...:', inputData.criteria);
                imap.search(inputData.criteria, (err, results) => {
                    if (err) {
                        reject(err);
                        return;
                    }
                    logger.info(results);
                    if (results.length === 0) {
                        logger.info('No new emails found.');
                        resolve(messages);
                        return;
                    }
                    const fetch = imap.fetch(results, { bodies: '', struct: true });
                    fetch.on('message', (msg, seqno) => {
                        logger.info(`Processing message #${seqno}`);
                        let rawEmail = '';
                        msg.on('body', (stream) => {
                            stream.on('data', (chunk) => {
                                rawEmail += chunk.toString();
                            });
                        });
                        msg.once('end', async () => {
                            try {
                                const parsed = await simpleParser(rawEmail);
                                messages.push({
                                    data: parsed,
                                    seqno: seqno
                                });
                                if (messages.length === results.length) {
                                    resolve(messages);
                                }
                            } catch (parseErr) {
                                logger.error('Error parsing email:', parseErr.message);
                            }
                        });
                    });
                    fetch.once('error', (err) => {
                        logger.error('Fetch error:', err.message);
                        reject(err);
                    });
                    fetch.once('end', () => {
                        logger.info('All messages processed.');
                        imap.end();
                    });
                });
            });
        });
        imap.once('error', function (err) {
            logger.error('IMAP Error', err.message);
            reject(err);
        });
        imap.once('end', function () {
            logger.info('Connection ended');
        });
        imap.connect();
    });
}

setInterval(() => {
    fetchEmails().then((messages) => {
        if (messages && messages.length > 0) {
            StartProcess({
                data: messages
            });
        }
    }).catch((err) => {
        logger.error('Error on Polling Mailbox :', err.message);
        logger.error(err);
    });
}, (inputData.pollFrequency || 10) * 1000);

const Kafka = require('node-rdkafka');
if (inputData.autoCommit == undefined || inputData.autoCommit == null) {
    inputData.autoCommit = false;
}
const kafkaOptions = {
    'metadata.broker.list': connectorData.servers,
    'security.protocol': connectorData.protocol,
    'group.id': inputData.groupId,
    'enable.auto.commit': inputData.autoCommit == true || inputData.autoCommit == 'true',
}
if (connectorData.sasl == 'true') {
    kafkaOptions['sasl.username'] = connectorData.username;
    kafkaOptions['sasl.password'] = connectorData.password;
    kafkaOptions['sasl.mechanisms'] = connectorData.mechanism.toUpperCase();
}
logger.trace('Kafka Options - ', kafkaOptions);
const consumer = new Kafka.KafkaConsumer(kafkaOptions, {
    'auto.offset.reset': 'earliest'
});
try {
    logger.info('Connecting consumer...');
    consumer.connect(null, (err, metadata) => {
        if (err) {
            logger.error('Error connecting to Kafka :: ', err);
            process.exit(0);
        } else {
            logger.debug('Metadata :: ', metadata);
        }
    });
    consumer.on('ready', () => {
        logger.info('Kafka Consumer is ready');
        const topic = inputData.topic;
        consumer.subscribe([topic]);
        logger.info(`Subscribed to topic: ${topic}`);
        consumer.consume();
    });
    consumer.on('event.error', (err) => {
        logger.error('Error from consumer:', err);
        process.exit(0);
    });
    consumer.on('data', (message) => {
        logger.debug(`Received message: ${message.value.toString()}`);
        logger.info(`Topic: ${message.topic}, Partition: ${message.partition}, Offset: ${message.offset}`);
        StartProcess({ message: message, data: message.value, consumer });
    });
} catch (err) {
    logger.error('Error in consumer:', err);
    process.exit(0);
}
process.on('SIGINT', function () {
    consumer.disconnect((err) => {
        logger.info('Disconnecting consumer...');
        if (err) {
            logger.info('Error disconnecting Consumer', err);
        } else {
            logger.info('Consumer disconnected');
        }
    });
});

const Kafka = require('node-rdkafka');

const kafkaOptions = {
    'metadata.broker.list': connectorData.servers,
    'security.protocol': connectorData.protocol
}
if (connectorData.sasl == 'true') {
    kafkaOptions['sasl.username'] = connectorData.username;
    kafkaOptions['sasl.password'] = connectorData.password;
    kafkaOptions['sasl.mechanisms'] = connectorData.mechanism.toUpperCase();
}

logger.trace('Kafka Options - ', kafkaOptions);

const producer = new Kafka.Producer(kafkaOptions);
const connectProducer = (producer) => {
    return new Promise((resolve, reject) => {
        producer.connect(null, (err, metadata) => {
            if (err) {
                logger.error('Error connecting to Kafka :: ', err);
                reject(err);
            } else {
                logger.debug('Metadata :: ', metadata);
            }
        });
        producer.on('ready', () => {
            logger.info('Kafka Producer is ready');
            resolve();
        });
        producer.on('event.error', (err) => {
            logger.error('Error from producer:', err);
            reject(err);
        });
    });
};

const disconnectProducer = (producer) => {
    return new Promise((resolve, reject) => {
        producer.disconnect((err) => {
            logger.info('Disconnecting producer...');
            if (err) {
                reject(err);
            } else {
                resolve();
            }
        });
    });
};

try {
    logger.info('Connecting producer...');
    await connectProducer(producer);
    logger.info('Producer is ready');
    const topic = inputData.topic;
    const message = Buffer.from(inputData.data);
    let status = producer.produce(topic, null, message, null, Date.now());
    if (status) {
        logger.info(`Message sent to topic "${topic}"`);
    }
    return { status };
} catch (err) {
    logger.error('Error in producer lifecycle:', err);
    return { statusCode: 500, message: err.message };
} finally {
    logger.info('Disconnecting producer...');
    disconnectProducer(producer);
    logger.info('Producer disconnected');
}

inputData.consumer.commitMessage(inputData.message);
logger.info(`Message offset ${inputData.message.offset} acknowledged.`);
return { status: true };
