async function executeNode(inputParams, context) {
  const oracledb = require('oracledb');
  let connection;
  try {
    const queryTimeout = (+(connectorData.queryTimeout || inputParams.queryTimeout || 60)) * 1000;
    connection = await oracledb.getConnection(connectorData.poolName);
    const queryPromise = connection.execute(inputParams.query, (inputParams.values || []));
    const result = await withTimeout(queryPromise, queryTimeout);
    return { success: true, output: { rows: result.rows } };
  } catch (err) {
    return { success: false, output: { error: err.message } };
  } finally {
    if (connection) {
      connection.close();
    }
  }

  function withTimeout(promise, ms) {
    return Promise.race([
      promise,
      new Promise((, reject) =>
        setTimeout(() => reject(new Error('Query timeout exceeded: ' + ms + 'ms')), ms)
      ),
    ]);
  }
}

