async function executeNode(inputParams, context) {
  const agents = inputParams.agents || [];
  logger.debug('Agent_Send_Dynamic - ', agents);
  const result = [];
  let finalStatusCode = 200;

  if (!Array.isArray(agents) || agents.length === 0) {
    return { success: false, output: { statusCode: 400, result } };
  }

  for (const agentData of agents) {
    try {
      if (!agentData.outputDirectories ||
        !Array.isArray(agentData.outputDirectories) ||
        agentData.outputDirectories.length === 0 ||
        agentData.outputDirectories.every(dir => dir === null || dir === undefined || dir === '')) {
        throw new Error('OutputDirectories is not configured for agentId: ' + agentData.agentId);
      }

      const agentInput = {
        agentId: agentData.agentId,
        outputDirectories: agentData.outputDirectories,
        outputFileName: agentData.outputFileName,
        decryptFile: agentData.decryptFile,
        extract: agentData.extract,
        data: agentData.fileData
      };

      logger.debug('AgenInput - ', agentInput);
      const agentDoc = await commonUtils.getAgent(agentData.agentId);
      logger.debug('AgentDoc - ', agentDoc);
      const downloadOptions = commonUtils.getAgentSendPayload(req, flowData, agentDoc, agentInput);
      logger.info('Sending File Download Request to ' + agentData.agentId + ' Agent');
      const fileDetails = await agentUtils.sendAgentDownloadRequestV1(flowData._id, req, downloadOptions);
      logger.debug('FileDetails - ', fileDetails);
      result.push({
        agentId: agentData.agentId,
        status: 'success',
        statusCode: 200,
        data: fileDetails
      });

    } catch (err) {
      logger.error('Error Occurred on ' + agentData.agentId + ' Agent Download', err);
      result.push({
        agentId: agentData.agentId,
        status: 'failure',
        statusCode: 400,
        data: { filename: agentData.outputFileName },
        error: err,
        message: err?.message || 'Unknown error'
      });
    }
  }

  const allSuccess = result.every(r => r.status === 'success');
  const anySuccess = result.some(r => r.status === 'success');

  if (!allSuccess && anySuccess) {
    finalStatusCode = 206;
  } else if (!anySuccess) {
    finalStatusCode = 500;
    throw new Error('Error Occurred on Agent_Send_Dynamic Node : ' + JSON.stringify(result));
  }

  return { success: true, output: { statusCode: finalStatusCode, result } };
}

